mapper.select:
SELECT COUNT(*)
FROM main_category
WHERE main_category_name = #{mainCategoryName}

mapper.update:
UPDATE main_category
SET main_category_name = #{mainCategory.mainCategoryName}
WHERE main_category_id = #{mainCategoryId}

mapper.delete:
DELETE FROM sub_category
WHERE main_category_id = #{mainCategoryId};

DELETE FROM main_category
WHERE main_category_id = #{mainCategoryId};
